import { ChannelOptions } from '@grpc/grpc-js';
/**
 * Configuration options for the Milvus client.
 */
export interface ClientConfig {
    address: string;
    token?: string;
    ssl?: boolean;
    username?: string;
    password?: string;
    channelOptions?: ChannelOptions;
    timeout?: number | string;
    maxRetries?: number;
    retryDelay?: number;
    database?: string;
}
export interface ServerInfo {
    build_tags?: string;
    build_time?: string;
    git_commit?: string;
    go_version?: string;
    deploy_mode?: string;
    reserved?: {
        [key: string]: any;
    };
}
